        /// <summary>
        /// 格式化发送列表
        /// </summary>
        /// <param name="content">内容</param>
        /// <returns>格式化后的列表</returns>
        internal static List<byte> FormatB4Send(string content)
        {
            List<byte> arr = new List<byte>();
            foreach (char ch in content)
            {
                string tmp = Convert.ToString(ch, 16);
                if (Convert.ToInt32(tmp, 16) > 0xFF)
                {
                    int high = Convert.ToInt32(tmp.Substring(0, 2), 16);
                    int low = Convert.ToInt32(tmp.Substring(2, 2), 16);
                    arr.Add((byte)low);
                    arr.Add((byte)high);
                }
                else
                {
                    int high = 0;
                    int low = Convert.ToInt32(tmp, 16);
                    arr.Add((byte)low);
                    arr.Add((byte)high);
                }
            }
            return arr;
        }

