                {
                    var tmpList = new List<byte>(header);
                    tmpList[4] = (byte)listIndex;
                    tmpList[10] = (byte)listIndex;
                    tmpList[11] = (byte)(list.Count + 0x40);
                    list.InsertRange(0, tmpList);
                    listIndex++;
                }

                List<List<byte>> result = new List<List<byte>>();
                result.Add(firstPart);
                foreach (var item in restPartList)
                {
                    result.Add(item);
                }
                return result;
            }

        }

        internal static List<List<byte>> SeparateList(List<byte> list, int chunkSize)
        {
            List<List<byte>> chunks = new List<List<byte>>();
            for (int i = 0; i < list.Count; i += chunkSize)
            {
                chunks.Add(list.GetRange(i, Math.Min(chunkSize, list.Count - i)));
            }
            return chunks;
        }

        /// <summary>
        /// 根据输入的IP地址格式，返回一个IP地址列表
        /// </summary>
        /// <param name="ip">IP地址（如: 192.168.1.1-100）</param>
        /// <returns>IP地址列表</returns>
        /// <exception cref="InvalidIPAddressException">IP格式错误</exception>
        internal static List<string> GetIp(string ip)
        {
            List<string> targetHost = new List<string>();
            if (!ip.Contains("."))
            {
                throw new InvalidIPAddressException(ip);
            }
            if (ip.Contains("-"))
            {
                string[] ipArr = ip.Split('-');
                string[] ipArrs = ipArr[0].Split('.');
                if (Convert.ToInt32(ipArr[1]) > 254)
                {
                    ipArr[1] = "254";
                }
                for (int i = Convert.ToInt32(ipArrs[3]); i <= Convert.ToInt32(ipArr[1]); i++)
                {
                    ipArrs[3] = i.ToString();
                    targetHost.Add(string.Join(".", ipArrs));
                }
            }
            else if (!ip.Contains("/"))
            {
                targetHost.Add(ip);
            }
            else if (ip.Contains("/24"))
            {
                string[] ipArr = ip.Split('/');
                string[] ipArrs = ipArr[0].Split('.');
                for (int i = 1; i <= 254; i++)
                {
                    ipArrs[3] = i.ToString();
                    targetHost.Add(string.Join(".", ipArrs));
                }
            }
            else
            {
                throw new InvalidIPAddressException(ip);
            }
            return targetHost;
        }

        /// <summary>
        /// 根据命令类型和内容，返回一个数据包列表
        /// </summary>
        /// <param name="cmdtype">命令类型（只能为<see cref="CommandType.SendMessage"/>或<see cref="CommandType.ExecuteCommand"/>）</param>
        /// <param name="content">内容</param>
        /// <returns>数据包</returns>
        internal static List<byte> PackageSendList(CommandType cmdtype, string content)
        {
            List<byte> arrs = FormatB4Send(content);
            List<byte> result = null;
            if (cmdtype == CommandType.SendMessage)
            {
                int index = 56;
                result = new List<byte>(basicCmd[CommandType.SendMessage]);
                foreach (int elem in arrs)
                {
                    result[index] = (byte)elem;
                    index++;
                }
            }
            else if (cmdtype == CommandType.ExecuteCommand)
            {
                int index = 100;
                result = new List<byte>(basicCmd[CommandType.ExecuteCommand]);
                foreach (int elem in arrs)
                {
                    result[index] = (byte)elem;
                    index++;
                }
            }
            return result;
        }

        /// <summary>
        /// 发送数据包
        /// </summary>
        /// <param name="sendList"></param>
        /// <param name="ip"></param>
        /// <param name="port"></param>
        /// <exception cref="Exception"></exception>
        internal static void Send(List<byte> sendList, string ip, int port)
        {
            if (sendList.Count == 0)
            {
                throw new Exception("发送列表为空。");
            }
            using (var client = new UdpClient())
            {
                List<string> targetHost = GetIp(ip);
                foreach (string i in targetHost)
                {
                    byte[] payload = sendList.ToArray();
                    client.Send(payload, payload.Length, i, port);
                }
            }
        }

