        /// <summary>
        /// 发送消息
        /// </summary>
        /// <param name="content">消息内容, 若<paramref name="cmdType"/>为<see cref="CommandType.Reboot"/>或<see cref="CommandType.Shutdown"/>则会忽略</param>
        /// <param name="cmdType">命令类型</param>
        /// <param name="ip">IP地址</param>
        /// <param name="port">端口</param>
        public static void SendMessage(string content, CommandType cmdType, string ip, int port)
        {
            if (cmdType == CommandType.SendMessage)
            {
                List<byte> sendList = CreateMessageSendList(content);
                Send(sendList, ip, port);
            }
            else if (cmdType == CommandType.ExecuteCommand)
            {
                List<byte> sendList = CreateCommandSendList(content);
                Send(sendList, ip, port);
            }
            else if (cmdType == CommandType.Reboot)
            {
                List<byte> sendList = CreateRebootSendList();
                Send(sendList, ip, port);
            }
            else if (cmdType == CommandType.Shutdown)
            {
                List<byte> sendList = CreateShutdownSendList();
                Send(sendList, ip, port);
            }
        }

        /// <summary>
        /// 获取极域的监听端口
        /// </summary>
        /// <returns>监听端口</returns>
        public static int[] GetMythwareListeningPorts()
        {
            var udpPorts = UdpPortFinder.GetUdpListeningPorts();
            foreach (var portInfo in udpPorts)
            {
                portInfo.ResolveProcessName();
            }
            var mythwarePorts = udpPorts.Where(p => p.ProcessName.Contains("StudentMain"));
            List<int> ports = new List<int>();
            foreach (var portInfo in mythwarePorts)
            {
                ports.Add(portInfo.LocalPort);
            }
            return ports.ToArray();
        }

